class Solution {public:    int getDecimalValue(ListNode* head) {    int sum = 0;    int num = 0;    ListNode* newhead = head;    vector<int> a(30);    int i = 0;    while (newhead != nullptr) {        num ++;        a[i++] = newhead -> val;        newhead = newhead -> next;    }    a.resize(num);    int m = 0,j = 0;    for (m = num - 1,j = 0; m >= 0, j < num; j++,m--) {         sum += a[m] * pow(2 , j);    }    return sum;    }};
